css:
 /*pc*/@media (min-width: 601px){
.top-bar{background: #fbfbfb;width: 100%;}
.header__nav {border-radius: 40px;background: #fff;
  text-align: center;text-transform: none;}
.header__nav {font-size: 18px;font-weight: 400;margin-top: 10px;}
.header__nav a {background: #fff;font-size: 18px;font-weight: 400;
    padding: 10px 20px 5px 5px;margin-bottom: 5px;}
.header__nav a:hover {background: transparent;border-bottom: 2px
solid #137ecd;font-size: 18px;font-weight: 700;
  padding: 5px 10px 5px 10px;margin-bottom: -10px;color: #253e8e;}
.product__name{font-size:18px;}
h1 {font-size: 25px;color:#1d3493;}
.lead {font-size: 18px;font-weight: 500;}
.prices__value{font-size: 21px;font-weight: 700;color:#137ecd;}
.prices__discount{font-size: 14px;font-weight: 700; color:#151289;}
.prices--big .prices__value {font-size: 30px;font-weight: 700;
color:#253e8e;}
.prices:not(.prices--big) .prices__installments {font-weight: 700;
  color:#253e8e;}   
.header__search-input {border-radius: 20px;font-size: 15px;
width: 400px;border: 1px dotted #9bbaf9;margin-left: 520px;
background: #f9f9f973;}
.header__search-btn {right: 22px;}
.header__logo {max-width: 170px;margin-top: -5px;max-height: 130px;
margin-left: 0px;}
#ws_icon_chat {
    position: fixed;
    bottom: 120px;
    left: 15px;
    z-index: 2000;
    cursor: pointer;
    outline: none !important;}
#ws_icon_chat img {width: 80px !important;}
.body{color:#141414;font-family: 'Gabarito', cursive;font-size: 18px;}
}

/*mobile*/@media (max-width: 600px) {
.header__logo {max-width: 120px!important;}
.prices__value{font-size: 16px;font-weight: 700;color:#137ecd;}
.body{color:#141414;font-family: 'Gabarito', cursive;font-size: 16px;}
#ws_icon_chat {
    position: fixed;
    bottom: 80px;
    left: 15px;
    z-index: 2000;
    cursor: pointer;
    outline: none !important;}
#ws_icon_chat img {width: 120px !important;}
.top-bar {background: #fbfbfb;width: 100%;}
.product__name{font-size:16px;}
h1 {font-size: 21px;}
.lead {font-size: 16px;font-weight: 500;}
.product-card__offer-stamp {background-color: #0fa8fd;
border-radius: 100px;}
.prices__discount{font-size: 14px;font-weight: 700;color:#14108c;}
.prices:not(.prices--big) .prices__installments{font-weight: 600;color:#000;}
.prices--big .prices__value {font-size: 20px;font-weight: 700;color:#253e8e;}
h4{color: #253e8e;}
}


 /*responsivo*/
.glide__bullets{display:none;}
.product-card__name {color: #343434;font-size: 18px;}
.body{font-family: 'Gabarito', cursive;}
.products-carousel__title:before {background: #0faefd;}
h4{color: #253e8e;}
h1 {color:#1d3493;}
.pb-lg-4{ padding-bottom: 0rem;}
.product__favorite i {color: #ff0d88;font-size:23px;}
.breadcrumb a {color: hsl(225.71deg 58.66% 35.1%);}
.header {background: #ffffff;border-bottom: 1px solid #233c8d;}
.share-network-whatsapp{display:none;}
.share-network-facebook{display:none;}
.share-network-twitter{display:none;}
.share-network-telegram{display:none;}
.share-network-pinterest{display:none;}
.share-network-email{display:none;}
.share-network-sms {display:none;}
.d-lg-inline {display: none!important;}
.product__short-stock{display: none;}
.search-engine__filter--Brands{ display:none;}
.search-engine__filter--price{ display:none;}
.shipping-line[data-service-code="PROPRIA"] span {display: none;}
.order-info__shipping-freight .shipping-line span {display: none;}
.product__short-stock{display: none;}
.footer {background:rgb(252,252,252);
background: -moz-linear-gradient(180deg, rgba(252,252,252,1) 0%, rgba(242,242,242,1) 58%, rgba(220,237,245,1) 96%);
background: -webkit-linear-gradient(180deg, rgba(252,252,252,1) 0%, rgba(242,242,242,1) 58%, rgba(220,237,245,1) 96%);
background: linear-gradient(180deg, rgba(252,252,252,1) 0%, rgba(242,242,242,1) 58%, rgba(220,237,245,1) 96%);
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#fcfcfc",endColorstr="#dcedf5",GradientType=1);
border-top: var(--spacer-1) solid #b9e0f3;}
.footer__store {color: #253e8e;font-weight: 700;}
.footer__title {color: #253e8e;font-weight: 700;}
.footer__links i {color: #f7f7f7;}
.col-12.col-xl.order-lg-first p {color:#253e8e;font-size: 16px;}
a {font-weight: 700;}
.i-whatsapp{color:#1b9ad7;}
.i-phone{color:#1b9ad7;}
.address{color: #253e8e;font-weight: 700;}
.footer__credits{border-top: var(--border-width) solid #e6eff3;}
.footer__credits {color: #253e8e;font-size: 12px;font-weight: 500;}
.btn-sm {border-radius: 0.3rem;font-size: .875rem;line-height: 1.2;
padding: 10px 10px;}
.btn-light {background-color: #a8d3ff;border-color: #7fbefd;}
.checkout__overlay.show {opacity: 0;z-index: 10;}
.btn-success {background-color: #253e8e;border-color: #253e8e;}
.btn-success a:hover{background-color: #18aaf3;border-color: #18aaf3;}
.btn-primary a:hover{background-color: #18aaf3;border-color: #18aaf3;
color: var(--primary-yiq);}
.menu__item {align-items: center;border-bottom: var(--border-width) dotted #f1f0f0;
color: var(--primary);}
.menu__item a:hover{align-items: center;background: #0faefd;color: #fff;}
.buy-together__title {text-align: center;color:#0faefd;font-size:21px;font-weight: 700;}
.shipping-line[data-service-code="PROPRIA"] span {display: none;}
.order-info__shipping-freight .shipping-line span {display: none;}
.list-group-item.active {background-color:#578eff;border-color:#578eff;
color: #fff;z-index: 2;}
.shipping-line>small {color:#fff;}
.top-bar{display:none;}
.cart__summary-row>span {color: #000;}
.checkout__overlay.show {opacity: 0;z-index: 10;}

}

