css:
 /*pc*/@media (min-width: 601px){
.top-bar{background: #fbfbfb;width: 100%;}
.header__nav {
    border-radius: 40px;
    background: #fff;
    text-align: center;
    text-transform: none;
}
.header__nav {
    font-size: 18px;
    font-weight: 400;
    margin-top: 10px;
}
.header__nav a {
    background: #fff;
    font-size: 18px;
    font-weight: 400;
    padding: 10px 20px 5px 5px;
    margin-bottom: 5px;
}
.header__nav a:hover {
    background: #18219d;
    font-size: 18px;
    font-weight: 700;
    padding: 5px 10px 5px 10px;
    margin-bottom: -10px;
    color: #fff;
}


.product__name{font-size:18px;}
h1 {font-size: 25px;color:#1d3493;}

.lead {
    font-size: 18px;
    font-weight: 500;font-family:"Raleway", Sans-serif;
}
.prices__value{font-size: 21px;
  font-weight: 700;
    font-family: "Raleway", Sans-serif;
    color: #0d723c;}
.prices__discount{font-size: 14px;
  font-weight: 700;font-family:"Raleway", Sans-serif; color:#151289;}
.prices--big .prices__value {
    font-size: 30px;font-weight: 700;font-family:"Raleway", Sans-serif; color:#253e8e;}
.prices:not(.prices--big) .prices__installments {
    font-weight: 700;color:#000;
}

.header__search-input {
    border-radius: 20px;
    font-size: 15px;
    width: 400px;
    border: 1px dotted #9bbaf9;
    margin-left: 520px;
  background: #f9f9f973;
}
   .header__search-btn {right: 22px;}
.header__logo {
    max-width: 300px;
    margin-top: -5px;
    max-height: 100px;
    margin-left: 30px;
}
#ws_icon_chat {
    position: fixed;
    bottom: 120px;
    left: 15px;
    z-index: 2000;
    cursor: pointer;
    outline: none !important;
}#ws_icon_chat img {
    width: 80px !important;
}
}

/*mobile*/@media (max-width: 600px) {
   .header__logo {
    max-width: 120px!important;


}

  #ws_icon_chat {
    position: fixed;
    bottom: 80px;
    left: 15px;
    z-index: 2000;
    cursor: pointer;
    outline: none !important;
}#ws_icon_chat img {
    width: 120px !important;
}

   .top-bar {
    background: #fbfbfb;
    width: 100%;
}
  .product__name{font-size:16px;}
